import './address.css'
import { Outlet, useNavigate } from "react-router-dom";

export const Address = () => {

  const navigate = useNavigate()

  const checkPaymentPath = () => {
    if (window.location.pathname === 'checkout/payment') {
      <html lang="en">
      <head>
        <meta charset="UTF-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <title>Document</title>
      </head>
      <body>
        
      </body>
      </html>
    }
  }

  useEffect(() => {
    checkPaymentPath()
  },[])

  return (
    <section className="address">
      <div className="container">
        <div className="row">
          <div className="col-8">
            <Outlet/>
          </div>
          <div className="col-4" style={{borderLeft: '1px solid lightgray'}}>
            <div className="right_card">
              <h6>Price details (2 items)</h6>
              <hr />
              <div className="amount">
                <span>Total Amount</span>
                <span> â‚¹ 3000 </span>
              </div>
              <hr />
              <div>
                <button onClick={()=>navigate('/checkout/payment')} id="proceed"> Proceed To Pay </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section >
  );
};
