import React from "react";
import offer from '../Assets/offers.png'

export const PaymentGateway = () => {
  return (
    <div className="payment-gateway">
      <div className="offers">
        <div> <img src={offer} alt="offer" /> <span>Bank Offers</span> </div>
        <ul className="offers-li">
          <li> 10% Instant Discount on Citi-branded Credit and Debit Cards on a minimum spend of ₹3,000. TCA </li>
          <li> 7.5% Instant Discount on every spends with Myntra Kotak Credit Card. TCA </li>
          <li> Get Assured Cashback up to ₹200 on Paytm UPI on a min spend of ₹500. TCA </li>
          <li> Up to ₹1,000 Cashback on CRED UPI  on a minimum spend of ₹1,000. TCA </li>
          <li> Flat ₹30 Cashback on Freecharge UPI (Android Devices only) on a minimum spend of ₹1,999. TCA </li>
        </ul>
      </div>
      
    </div>
  );
};
