import React, { useEffect, useState } from "react";
import './navbar.css'
import logo from '../Assets/logo.png'
import cart_icon from '../Assets/cart_icon.png'
import { Link, useLocation } from "react-router-dom";
import { useCustom } from "../../Context/ShopContext";

export const Navbar = () => {
  const { getTotalCartItems } = useCustom()
  const [menu, setMenu] = useState(localStorage.getItem('menu'));
  useEffect(() => {
    localStorage.setItem('menu', menu)
  }, [menu])

  const location = useLocation()
  console.log(location.pathname)

  return (
    <div className="navbar">
      <div className="nav-logo">
        <img src={logo} alt="" />
        <p>SHOPPER</p>
      </div>
      <ul className="nav-menu">
        <li onClick={() => setMenu('shop')}><Link to="/">Shop</Link> {menu === "shop" ? <hr /> : <></>} </li>
        <li onClick={() => setMenu('men')}><Link to="/mens">Men</Link>{menu === "men" ? <hr /> : <></>} </li>
        <li onClick={() => setMenu('women')}><Link to="/womens">Women</Link>{menu === "women" ? <hr /> : <></>} </li>
        <li onClick={() => setMenu('kids')}><Link to="/kids">Kids</Link>{menu === "kids" ? <hr /> : <></>} </li>
      </ul>
      <div className="nav-login-cart">
        <Link to="/login">
          <button> Login</button>
        </Link>
        <Link to="/cart">
          <img src={cart_icon} alt="" />
        </Link>
        <div className="nav-cart-count">
          {getTotalCartItems()}
        </div>
      </div>
    </div>
  );
};
