import React from "react";
import './payment.css'
import offer from '../Assets/offers.png'

export const PaymentGateway = () => {

  const viewMore = () => {
    const li = document.querySelectorAll('.offers-li li')
    const viewMore = document.querySelector('.view-more')
    // if ()

      li.forEach(list => {
        if (list.classList.contains('d-none')) {
          list.classList.remove('d-none')
        }

      })
  }

  return (
    <div className="payment-gateway">
      <div className="offerss">
        <div> <img src={offer} alt="offer" /> <span>Bank Offers</span> </div>
        <ul className="offers-li">
          <li> 10% Instant Discount on Citi-branded Credit and Debit Cards on a minimum spend of ₹3,000. TCA </li>
          <li className="d-none"> 7.5% Instant Discount on every spends with Myntra Kotak Credit Card. TCA </li>
          <li className="d-none"> Get Assured Cashback up to ₹200 on Paytm UPI on a min spend of ₹500. TCA </li>
          <li className="d-none"> Up to ₹1,000 Cashback on CRED UPI  on a minimum spend of ₹1,000. TCA </li>
          <li className="d-none"> Flat ₹30 Cashback on Freecharge UPI (Android Devices only) on a minimum spend of ₹1,999. TCA </li>
        </ul>
        <span className="view-more" onClick={viewMore}>View More <i class="fa-solid fa-angle-down"></i> </span>
      </div>
      <h5> Choose Payment Mode </h5>
      <div className="payment-mode">

      </div>
    </div>
  );
};
