import React, { useEffect, useState } from "react";
import "./items.css";
import { Link } from "react-router-dom";
import {logo} from '/public/assets/product/1713264723516.jpg'

export const Items = (props) => {

  const [image, setImage] = useState([]);

  useEffect(() => {
    setImage(props.image.split(','))
  }, [])
  
  console.log(image)

  return (
    <div className="item col-6 col-md-3 col-lg-3 p-3">
      <Link to={`/product/${props.id}`}>
        <img src={logo} alt="Products" title="View Details" onClick={window.scrollTo(0, 0)} />
      </Link>
      <p className="p-2">{props.name}</p>
      <div className="item-prices px-2">
        <div className="item-price-new">
          ₹{props.new_price}
        </div>
        <div className="item-price-old">
          ₹{props.old_price}
        </div>
      </div>
    </div>
  );
};
