import React, { useEffect, useState } from "react";
import './popular.css'
import data_product from '../Assets/data'
import { Items } from "../items/Items";
import axios from 'axios';

export const Popular = () => {

  const [data, setData] = useState(JSON(localStorage.getItem('data')) || []);

  const fetchData = async () => {
    try {
      await axios.get('http://localhost:8005/shopper/product/get').then(res => {
        // console.log(res.data.result)
        // setData(res?.data?.result)
        localStorage.setItem('data', res?.data?.result)
      })
    }
    catch (error) {
      console.log(error)
    }
  }

  useEffect(() => {
    fetchData()
  }, [])
  console.log(data)

  return (
    <div className="popular">
      <h1>POPULAR IN WOMEN </h1>
      <hr />
      <div className="popular-item row">
        {data.map((item, i) => {
          return <Items key={i} id={item.id} name={item.Title} image={item.image} new_price={item.new_price} old_price={item.old_price} />
        })}
      </div>
    </div>
  );
};
