import React, { useEffect, useState } from "react";
import './shop.css'
import { useCustom } from "../../Context/ShopContext";
import dropdown_icon from "../../Components/Assets/dropdown_icon.png";
import { Items } from "../../Components/items/Items";
import right_arrow from "../../Components/Assets/breadcrum_arrow.png";


export const ShopCategory = (props) => {

  const { all_product } = useCustom()
  const [currentProduct, setCurrentProduct] = useState([])
  let x = 8;
  useEffect(() => {
    for (let i = 0; i < all_product.length; i++) {
      if (props.category === all_product[i].category) {
        currentProduct[i] = all_product[i];
      }
    }
  }, [currentProduct])


  return (
    <div className="shop-category">
      <img src={props.banner} className="shopcategory-banner" alt="" />
      <div className="shopcategory-indexSort">
        <p>
          <span>Showing 1-12</span> out of 36 products
        </p>
        <div className="shopcategory-sort">
          Sort by <img src={dropdown_icon} alt="" />
        </div>
      </div>
      <div className="shopcategory-products">
        {currentProduct.map((item, i) => {
          console.log(i)
          if (props.category === item.category) {
            if (i < x) {
              return <Items key={i} id={item.id} name={item.name} image={item.image} new_price={item.new_price} old_price={item.old_price} />
            }
          } else {
            return null
          }
        })}
      </div>
      <div className="shopcategory-loadmore" onClick={() => exploreMore()}>
        Explore More
        <span>
          <img src={right_arrow} alt="" />
          <img src={right_arrow} alt="" />
        </span>
      </div>
    </div>
  );
};
