import axios from "axios";
import React, { useState } from "react";

export const UpdateAddress = ({id}) => {

  const user = JSON.parse(localStorage.getItem('user'))
  // const updateRow = JSON.parse(localStorage.getItem('updateRow'))
  const [updateRow, setUpdateRow] = useState([])

  const [address, setAddress] = useState({
    u_id: user._id,
    name: updateRow ? updateRow.name : '',
    mobile: 'updateRow.mobile',
    pin: 'updateRow.pin',
    house: 'updateRow.house',
    town: 'updateRow.town',
    city: 'updateRow.city',
    state: 'updateRow.state',
    save_as: 'updateRow.save_as',
    default: 'updateRow.default'
  })

  const inputChange = (e) => {
    e.preventDefault()
    setAddress((prev) => ({ ...prev, [e.target.name]: e.target.value }))
  }

  console.log(updateRow)
  const handleSaveAs = (key) => {
    let home = document.querySelector(".Home")
    let work = document.querySelector(".Work")
    let other = document.querySelector(".Other")
    setAddress((prev) => ({ ...prev, save_as: key }))
    if (home.classList.contains(key)) {
      home.classList.add('default')
      work.classList.remove('default')
      other.classList.remove('default')
    } else if (work.classList.contains(key)) {
      home.classList.remove('default')
      other.classList.remove('default')
      work.classList.add('default')
    } else {
      home.classList.remove('default')
      other.classList.add('default')
      work.classList.remove('default')
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    try {
      const res = await axios.post('http://localhost:8005/shopper/user/address/add', address)

      if (res.data) {
        window.location.reload();
      }
    } catch (error) {
      console.log(error)
    }
  }

  const updateData = async (id) => {
    try {

      const selectedItem = address.find(elem => elem._id === id);

      localStorage.setItem('updateRow', JSON.stringify(selectedItem));
      setUpdateRow(selectedItem)
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  }


  return (
    <>
      <div className="left_card">
        <form method="post" onSubmit={handleSubmit}>
          <div className="contact">
            <h6>Contact Details</h6>
            <input type="text" name="name" className="form-control" placeholder="Name" value={address.name} onChange={inputChange} />
            <input type="number" name="mobile" className="form-control" placeholder="Mobile No" value={address.mobile} onChange={inputChange} />
          </div>
          <div className="address2">
            <h6>Address</h6>
            <input type="number" name="pin" className="form-control" placeholder="Pin Code" value={address.pin} onChange={inputChange} />
            <input type="text" name="house" className="form-control" placeholder="House No, Street, Area" value={address.address} onChange={inputChange} />
            <input type="text" name="town" className="form-control" placeholder="Locality/Town" value={address.landmark} onChange={inputChange} />
            <div className="city_state">
              <input type="text" name="city" className="form-control" placeholder="City/District" value={address.city} onChange={inputChange} />
              <input type="text" name="state" className="form-control" placeholder="State" value={address.state} onChange={inputChange} />
            </div>
          </div>
          <div className="save_as">
            <h6>Save Address As</h6>
            <div>
              <span className={`btn Home ${address.save_as === 'Home' ? 'default' : ''}`} onClick={() => handleSaveAs('Home')}> Home </span>
              <span className={`btn Work ${address.save_as === 'Work' ? 'default' : ''}`} onClick={() => handleSaveAs('Work')}> Work </span>
              <span className={`btn Other ${address.save_as === 'Other' ? 'default' : ''}`} onClick={() => handleSaveAs('Other')}> Other </span>
            </div>
          </div>
          <div className="form-check">
            <input type="checkbox" name="default" id="flexCheckDefault" checked={address.default}
              onChange={(e) => setAddress((prev) => ({ ...prev, default: e.target.checked }))} />
            <label className="form-check-label" htmlFor="flexCheckDefault">
              Make Address default
            </label>
          </div>
          <div className="add_address">
            <button> Add Address </button>
          </div>
        </form>
      </div>
    </>
  );
};
