import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { AddNewAddress } from "./AddNewAddress";
import home from '../Assets/home-address.png'
import axios from "axios";

export const MoreAddress = () => {

  const arr = [{}, {}]
  const [address, setAddress] = useState()
  const user = JSON.parse(localStorage.getItem('user'))

  const getAddress = async () => {
    try {
      const res = await axios.get(`http://localhost:8005/shopper/user/address/get/${user._id}`)
      setAddress(res.data)
    } catch (error) {
      console.log(error)
    }
  }

  useEffect(() => {
    getAddress();
  },[])

  return (
    <div className="left_address">
      <div className="top_section">
        <h5>Select Delivery Address </h5>
        <button className="btn modal-btn" data-bs-toggle="modal" data-bs-target="#exampleModal"> <i class="fa-solid fa-circle-plus"></i> Add New Address </button>
        <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div class="modal-dialog modal-dialog-centered">
            <div class="modal-content">
              <div class="modal-header">
                <h1 class="modal-title fs-5" id="exampleModalLabel">Add New Address</h1>
                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
              </div>
              <div class="modal-body">
                <AddNewAddress />
              </div>
              <div class="modal-footer">
                <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                <button type="button" class="btn btn-primary">Save changes</button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="bottom_section">
        <h6> Addresses </h6>
        <div className="address_card">
          {
            arr != '' ?
              <>
                {
                  arr.map((item, i) => {
                    return (
                      <div className="address-one" key={i}>
                        <input type="radio" name="address" id="" checked />
                        <div className="address-right">
                          <div className="name">
                            <h6>Name <span class="badge rounded-pill text-bg-success px-2">Office</span></h6>
                            <div class="dropdown">
                              <span class="dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                                <i class="fa-solid fa-ellipsis"></i>
                              </span>
                              <ul class="dropdown-menu">
                                <li><Link class="dropdown-item" href="#">Edit</Link></li>
                                <li><Link class="dropdown-item" href="#">Delete</Link></li>
                              </ul>
                            </div>
                          </div>
                          <div className="add_one">
                            <span>House</span>, <span>Town</span>, <span>City</span>, <span>State</span> <span> - </span> <span>Pin</span>
                          </div>
                          <div className="mobile">Mobile: <span>0000000000</span></div>
                        </div>
                      </div>
                    )
                  })
                }
              </>
              :
              <div className="no-address">
                <img src={home} alt="no address" />
                <span> No Address Available. Please <span className="span" data-bs-toggle="modal" data-bs-target="#exampleModal"> Add New!</span> </span>
              </div>
          }
        </div>
      </div>
    </div>
  );
};
