import React, { createContext, useContext, useState } from "react";
import all_product from '../Components/Assets/all_product'
import axios from "axios";

const shopperData = JSON.parse(localStorage.getItem('data'))

export const ShopContext = createContext(null)

const getDefaultCart = () => {
  let cart = {};
  for (let i = 0; i < shopperData.length + 1; i++) {
    cart[i] = 0
  }
  return cart
}
const ShopContextProvider = (props) => {

  const [cartItems, setCartItems] = useState(getDefaultCart());

  const addToCart = async (itemId) => {
    try {
      await axios.post('http://localhost:8005/shopper/cart/addtocart',{p_id: itemId, } )
    } catch (error) {

    }
    setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }))
  }
  const removeFromCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }))
  }

  const getTotalCartAmount = () => {
    let totalAmount = 0
    for (let item in cartItems) {
      if (Number(cartItems[item]) > 0) {
        let itemInfo = shopperData.find((product) => product._id === item)
        totalAmount = totalAmount + itemInfo.new_price * cartItems[item]
      }
    }
    return totalAmount;
  }

  const getTotalCartItems = () => {
    let totalItem = 0;
    for (const item in cartItems) {
      if (cartItems[item] > 0) {
        totalItem += cartItems[item]
      }
    }
    return totalItem
  }


  const contextValue = { all_product, cartItems, addToCart, removeFromCart, getTotalCartAmount, getTotalCartItems }

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  )
}

export const useCustom = () => {
  return useContext(ShopContext);
}

export default ShopContextProvider
