import React, { useEffect, useState } from "react";
import './cartItems.css'
import { useCustom } from "../../Context/ShopContext";
import remove_icon from "../Assets/cart_cross_icon.png";
import axios from "axios";
import { Link } from "react-router-dom";


export const CartItems = () => {

  const { cartItems, removeFromCart } = useCustom()
  const products = JSON.parse(localStorage.getItem('data'))

  const cart = (JSON.parse(localStorage.getItem('cart')))

  const getTotalCartAmount = () => {
    var price = 0
    if (cart) {
      cart.product.forEach(elem => {
        products.forEach(item => {
          if (elem.p_id === item._id) {
            price += item.new_price * elem.nop
          }
        });
      });
      return price;
    }
  }

  const handleNOP = async (e, p_id, u_id) => {
    e.preventDefault();
    const nops = e.target.value;
    if (cart.product.p_id === p_id) {
      try {
        await axios.post('http://localhost:8005/shopper/cart/addtocart', { p_id: p_id, u_id: u_id, nop: nops }).then((res) => {
          console.log(res);
          localStorage.setItem('cart', JSON.stringify(res?.data?.result))
        })
      } catch (error) {
        console.log(error);
      }
    }
  }

  return (
    <div className="cartitems">
      <div className="container">
        <table className="table table-striped" style={{ width: '100%' }}>
          <thead className="">
            <tr>
              <th>#</th>
              <th scope="col">Products</th>
              <th scope="col">Title</th>
              <th scope="col">Price</th>
              <th scope="col">Quantity</th>
              <th scope="col">Total</th>
              <th scope="col" className="col-1" style={{ textAlign: 'center' }}>Del</th>
            </tr>
          </thead>
          {cart ?
            <tbody >
              {cart.product.map((e, ix) => {
                const { p_id, nop } = e;
                return (
                  <div >
                    {
                      products.map((item, i) => {
                        if (p_id === item._id) {
                          const img0 = item.images.split(',')[0]
                          return (
                            <tr className="cartitems-format" key={i}>
                              <th style={{fontSize: '13px'}}>{ix + 1}</th>
                              <td>
                                <img src={img0} alt="images" className="cartitems-product-icon" />
                              </td>
                              <td onClick={window.scrollTo(0, 0)} ><Link to={`/product/${item._id}`}>{item.Title}</Link> </td>
                              <td>₹{item.new_price} </td>
                              <td>
                                <input type="number" className="cartitems-quantity" value={nop} onChange={(event) => handleNOP(event, p_id, e.u_id)} />
                              </td>
                              <td>₹{item.new_price * nop} </td>
                              <td>
                                <img src={remove_icon} className="cartitems-remove-icon" alt="" onClick={() => removeFromCart(p_id)} />
                              </td>
                            </tr>
                          )
                        }
                      })
                    }
                  </div>
                )
              })}
            </tbody> : <></>
          }
        </table>
        <div className="cartitems-down row">
          <div className="cartitems-total col-12 col-md-6 col-lg-6 order-lg-first order-md-first">
            <h1>Cart Total</h1>
            <div>
              <div className="cartitems-total-item">
                <p>Subtotal</p>
                <p>₹{getTotalCartAmount()}</p>
              </div>
              <hr />
              <div className="cartitems-total-item">
                <p>Shipping Fee</p>
                <p>Free</p>
              </div>
              <hr />
              <div className="cartitems-total-item">
                <p>Total</p>
                <p>₹{getTotalCartAmount()}</p>
              </div>
            </div>
            <button>CHECHOUT</button>
          </div>
          <div className="cartitems-promocode col-12 col-md-6 col-lg-5 order-first">
            <p>If you have a promo code, Enter it here</p>
            <div className="cartitems-promobox">
              <input type="text" placeholder="Promo Code" />
              <button>Submit</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
