import React, { useState } from "react";
import "./items.css";
import { Link } from "react-router-dom";


export const Items = (props) => {

  const [hover, setHover] = useState(false);

  const onHover = (e) => {
    e.preventDefault();
    setHover(true); // turn true
    console.log("hovered");
  };

  const onHoverOver = (e) => {
    e.preventDefault(); // turn false
    setHover(false);
  };

  return (
    <>
    <div className="item col-6 col-md-3 col-lg-3 p-3" onMouseEnter={(e) => onHover(e)}
    onMouseLeave={(e) => onHoverOver(e)}>
      <Link to={`/product/${props.id}`}>
        {hover &&
          <div className="" style={{
            position: "absolute", textAlign: 'center', display: 'block', margin: 'auto'
          }}>
            <button>hello</button></div>
        }
        <img src={props.image} alt="" onClick={window.scrollTo(0, 0)}/>
      </Link>
      <p className="p-2">{props.name}</p>
      <div className="item-prices px-2">
        <div className="item-price-new">
          ₹{props.new_price}
        </div>
        <div className="item-price-old">
          ₹{props.old_price}
        </div>
      </div>
      </div>
      </>
  );
};
